mapper.select:
select *
from topic

	1=1

		and `id` = #{id}


		and `name` = #{name}


		and `origin_name` = #{originName}


		and `dpt_name` = #{dptName}


		and `owner_ids` = CONCAT('%',#{ownerNames},'%')


		and `owner_names` like CONCAT('%',#{ownerNames},'%')


		and `emails` = #{emails}


		and `tels` = #{tels}


		and `expect_day_count` = #{expectDayCount}


		and `business_type` = #{businessType}


		and `save_day_num` = #{saveDayNum}


		and `remark` = #{remark}


		and `token` = #{token}


		and `normal_flag` = #{normalFlag}


		and `topic_type` = #{topicType}


		and `max_lag` = #{maxLag}


		and `insert_by` = #{insertBy}


		and `insert_time` = #{insertTime}


		and `update_by` = #{updateBy}


		and `update_time` = #{updateTime}


		and `is_active` = #{isActive}



		and update_time >= #{updateTimeStart}


		and update_time  <=  #{updateTimeEnd}


		and insert_time >= #{insertTimeStart}


		and insert_time  <=  #{insertTimeEnd}


order by id desc
limit #{start1}, #{offset1}

select count(1)
from topic

	1=1

		and `id` = #{id}


		and `name` = #{name}


		and `origin_name` = #{originName}


		and `dpt_name` = #{dptName}


		and `owner_ids` = CONCAT('%',#{ownerNames},'%')


		and `owner_names` like CONCAT('%',#{ownerNames},'%')


		and `emails` = #{emails}


		and `tels` = #{tels}


		and `expect_day_count` = #{expectDayCount}


		and `business_type` = #{businessType}


		and `save_day_num` = #{saveDayNum}


		and `remark` = #{remark}


		and `token` = #{token}


		and `normal_flag` = #{normalFlag}


		and `topic_type` = #{topicType}


		and `max_lag` = #{maxLag}


		and `insert_by` = #{insertBy}


		and `insert_time` = #{insertTime}


		and `update_by` = #{updateBy}


		and `update_time` = #{updateTime}


		and `is_active` = #{isActive}



		and update_time >= #{updateTimeStart}


		and update_time  <=  #{updateTimeEnd}


		and insert_time >= #{insertTimeStart}


		and insert_time  <=  #{insertTimeEnd}
